import React, { useEffect, useState } from 'react';
import axios from 'axios';
import QRCode from 'react-qr-code';

const KidsInformations = ({ parentId }) => {
  const [kids, setKids] = useState([]);

  useEffect(() => {
    const fetchKids = async () => {
      const response = await axios.get(`/enfants/${parentId}`);
      setKids(response.data);
    };

    fetchKids();
  }, [parentId]);

  return (
    <div>
      {kids.map((kid) => (
        <div className="badge" key={kid.id}>
          <img src={kid.image} alt="Kid Picture" className="badge-image" />
          <div className="badge-content">
            <h3 className="badge-name">{kid.firstName} {kid.lastName}</h3>
            <p className="badge-birthdate">{kid.birthDate}</p>
            <p className="badge-class">{kid.class}</p>
          </div>
          <div className="badge-qrcode">
            <QRCode value={`Name: ${kid.firstName} ${kid.lastName}\nBirthdate: ${kid.birthDate}\nParent: ${kid.parent.firstName} ${kid.parent.lastName}\nEmail: ${kid.parent.email}`} />
          </div>
        </div>
      ))}
    </div>
  );
};

export default KidsInformations;
