import React, { useState } from "react";
import axios from "axios";

function SignUp() {
  const [formData, setFormData] = useState([
    {
      nomParent: "",
      prenomParent: "",
      mail: "",
      tel: "",
      nomEnfant: "",
      prenomEnfant: "",
      dateNaissance: "",
    },
  ]);

  const handleAddForm = () => {
    setFormData([
      ...formData,
      {
        nomParent: "",
        prenomParent: "",
        mail: "",
        tel: "",
        nomEnfant: "",
        prenomEnfant: "",
        dateNaissance: "",
      },
    ]);
  };

  const handleRemoveForm = (index) => {
    const newFormData = [...formData];
    newFormData.splice(index, 1);
    setFormData(newFormData);
  };

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const newFormData = [...formData];
    newFormData[index][name] = value;
    setFormData(newFormData);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:8080/addEnfantPreinscrit", formData)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  return (
    <div>
      <form onSubmit={handleSubmit}>
        {formData.map((input, index) => (
          <div key={index}>
            <input
              type="text"
              name="nomParent"
              placeholder="Parent Last Name"
              value={input.nomParent}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="prenomParent"
              placeholder="Parent First Name"
              value={input.prenomParent}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="email"
              name="mail"
              placeholder="Parent Email"
              value={input.mail}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="tel"
              name="tel"
              placeholder="Parent Phone Number"
              value={input.tel}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="nomEnfant"
              placeholder="Child Last Name"
              value={input.nomEnfant}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="prenomEnfant"
              placeholder="Child First Name"
              value={input.prenomEnfant}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="date"
              name="dateNaissance"
              placeholder="Child Birthdate"
              value={input.dateNaissance}
              onChange={(e) => handleInputChange(e, index)}
            />
            {index > 0 && (
              <button type="button" onClick={() => handleRemoveForm(index)}>
                Remove
              </button>
            )}
          </div>
        ))}
        <button type="button" onClick={handleAddForm}>
          Add More
        </button>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}export default SignUp;
