const handleSubmit = (event) => {
    event.preventDefault();

    // Create an object to hold the form data
    const formData = {
      prenom_parent: parentName,
      nom_parent: parentLastname,
      tel: parentPhone,
      email: parentEmail,
      enfants: kids.map((child) => ({
        nom_enfant: child.lastname,
        prenom_enfant: child.name,
        date_naissance: new Date(), // replace with actual birthdate
      })),
    };

    // Make a POST request to the server
    fetch('http://localhost:8080/addEnfantPreinscrit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful response
          console.log('Form submitted successfully');
          // Clear form fields
          setParentName('');
          setParentLastname('');
          setParentPhone('');
          setParentEmail('');
          setKids([{ name: '', age: '', lastname: '' }]);
          setNumKids(1);
          // Display success message to user
          alert('Form submitted successfully!');
        } else {
          // Handle error response
          console.error('Error submitting form');
          // Display error message to user
          alert('Error submitting form. Please try again.');
        }
      })
      .catch((error) => {
        // Handle network error
        console.error('Network error:', error);
        // Display error message to user
        alert('Network error. Please try again later.');
      });
  };
