const handleSubmit = async (event) => {
  event.preventDefault();

  try {
    // Make a POST request to the backend API with form data
    const response = await axios.post('/addExenfant', {
      firstName,
      lastName,
      email,
      birthdate,
      phoneNumber,
    });

    // Reset form fields after successful submission
    setFirstName('');
    setLastName('');
    setEmail('');
    setBirthdate('');
    setPhoneNumber('');

    // Optionally, you can display a success message or redirect the user to another page
    console.log('Form submitted successfully');
  } catch (error) {
    console.error('Error submitting form:', error);
    // Handle errors, such as displaying error messages to the user
    if (error.response && error.response.data && error.response.data.errors) {
      setErrors(error.response.data.errors);
    } else {
      setErrors({ general: 'Une erreur s\'est produite lors de l\'envoi du formulaire.' });
    }
  }
};
