import React, { useEffect, useState } from 'react';
import axios from 'axios';
import QRCode from 'react-qr-code';
// import './KidsInformations.css';

const kidsinformations = () => {
  const [kids, setKids] = useState([]);
  const currentUserId = 1; // replace this with the actual user ID

  useEffect(() => {
    const fetchkids = async () => {
      try {
        const response = await axios.get('/enfants');
        setKids(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchKids();
  }, []);

  const vosEnfants = kids.filter((kid) => kid.parentId === currentUserId);

  return (
    <div className="kids-container">
      {vosEnfants.map((kid) => (
        <div key={kid.id} className="badge">
          <img src={kid.picture} alt={`${kid.firstName} ${kid.lastName}`} className="badge-image" />
          <div className="badge-content">
            <h3 className="badge-name">{`${kid.firstName} ${kid.lastName}`}</h3>
            <p className="badge-birthdate">{kid.birthDate}</p>
          </div>
          <QRCode value={`https://example.com/enfants/${kid.id}`} className="badge-qrcode" />
        </div>
      ))}
    </div>
  );
};

export default kidsinformations;
