import React, { useState } from 'react';

export default function SignUp() {
  const [parentFirstName, setParentFirstName] = useState('');
  const [parentLastName, setParentLastName] = useState('');
  const [parentEmail, setParentEmail] = useState('');
  const [parentPhoneNumber, setParentPhoneNumber] = useState('');
  const [kids, setKids] = useState([{ prenomEnfant: '', nomEnfant: '', dateNaissance: '' }]);

  const handleAddKid = () => {
    setKids([...kids, { prenomEnfant: '', nomEnfant: '', dateNaissance: '' }]);
  };

  const handleRemoveKid = (index) => {
    const updatedKids = [...kids];
    updatedKids.splice(index, 1);
    setKids(updatedKids);
  };

  const handleKidChange = (index, event) => {
    const updatedKids = [...kids];
    updatedKids[index][event.target.name] = event.target.value;
    setKids(updatedKids);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = {
      prenomParent: parentFirstName,
      nomParent: parentLastName,
      tel: parentPhoneNumber,
      mail: parentEmail,
      enfants: kids
    };

    // Make a POST request to the server
    fetch('http://localhost:8080/addEnfantPreinscri', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful response
          console.log('Form submitted successfully');
          // Clear form fields
          setParentFirstName('');
          setParentLastName('');
          setParentEmail('');
          setParentPhoneNumber('');
          setKids([{ prenomEnfant: '', nomEnfant: '', dateNaissance: '' }]);

          // Display success message to user
          alert('Form submitted successfully!');
        } else {
          // Handle error response
          console.error('Error submitting form');
          // Display error message to user
          alert('Error submitting form. Please try again.');
        }
      })
      .catch((error) => {
        // Handle network error
        console.error('Network error:', error);
        // Display error message to user
        alert('Network error. Please try again later.');
      });
  };

  return (
    <div>
      <h1 className='sign-up'>pré-inscrie maintenant!</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="parent-first-name">Prénom du parent:</label>
          <input
            type="text"
            id="parent-first-name"
            value={parentFirstName}
            onChange={(event) => setParentFirstName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-last-name">Nom du parent:</label>
          <input
            type="text"
            id="parent-last-name"
            value={parentLastName}
            onChange={(event) => setParentLastName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-email">Votre email:</label>
          <input
            type="email"
            id="parent-email"
            value={parentEmail}
            onChange={(event) => setParentEmail(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-phone">Numéro de téléphone:</label>
          <input
            type="tel"
            id="parent-phone"
            value={parentPhoneNumber}
            onChange={(event) => setParentPhoneNumber(event.target.value)}
          />
        </div>
        {kids.map((kid, index) => (
          <div key={index}>
            <div>
              <label htmlFor={`kid-first-name-${index}`}>Prénom de l'enfant:</label>
              <input
                type="text"
                id={`kid-first-name-${index}`}
                name="prenomEnfant"
                value={kid.prenomEnfant}
                onChange={(event) => handleKidChange(index, event)}
              />
            </div>
            <div>
              <label htmlFor={`kid-last-name-${index}`}>Nom de l'enfant:</label>
              <input
                type="text"
                id={`kid-last-name-${index}`}
                name="nomEnfant"
                value={kid.nomEnfant}
                onChange={(event) => handleKidChange(index, event)}
              />
            </div>
            <div>
              <label htmlFor={`kid-age-${index}`}>Date de naissance de l'enfant:</label>
              <input
                type="date"
                id={`kid-age-${index}`}
                name="dateNaissance"
                value={kid.dateNaissance}
                onChange={(event) => handleKidChange(index, event)}
              />
            </div>
            <button type="button" onClick={() => handleRemoveKid(index)}>
              Supprimer cet enfant
            </button>
          </div>
        ))}
        <button type="button" onClick={handleAddKid}>
          Ajouter un enfant
        </button>
        <button type="submit">SUIVANT</button>
      </form>
    </div>
  );
}
