import React, { useState } from 'react';

export default function SignUp() {
  const [parentName, setParentName] = useState('');
  const [parentLastname, setParentLastname] = useState('');
  const [parentPhone, setParentPhone] = useState('');
  const [parentEmail, setParentEmail] = useState('');
  const [kids, setKids] = useState([{ name: '', age: '', lastname: '' }]);
  const [numKids, setNumKids] = useState(1);

  const handleChange = (event, index) => {
    const newKids = [...kids];
    newKids[index] = {
      ...newKids[index],
      [event.target.name]: event.target.value,
    };
    setKids(newKids);
  };

  const handleAddChild = () => {
    setKids([...kids, { name: '', age: '', lastname: '' }]);
    setNumKids(numKids + 1);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Create an object to hold the form data
    const formData = {
      nomParent: parentName,
      nomParent: parentLastname,
      tel: parentPhone,
      mail: parentEmail,
      enfants: kids.map((child) => ({
        nomEnfant: child.lastname,
        prenomEnfant: child.name,
        date_naissance: new Date(), // replace with actual birthdate
      })),
    };

    // Make a POST request to the server
    fetch('http://localhost:8080/addEnfantPreinscrit', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful response
          console.log('Form submitted successfully');
          // Clear form fields
          setParentName('');
          setParentLastname('');
          setParentPhone('');
          setParentEmail('');
          setKids([{ name: '', age: '', lastname: '' }]);
          setNumKids(1);
          // Display success message to user
          alert('Form submitted successfully!');
        } else {
          // Handle error response
          console.error('Error submitting form');
          // Display error message to user
          alert('Error submitting form. Please try again.');
        }
      })
      .catch((error) => {
        // Handle network error
        console.error('Network error:', error);
        // Display error message to user
        alert('Network error. Please try again later.');
      });
  };

  return (
    <div>
      <h1 className='sign-up'>pré-inscrie maintenant!</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="parent-name">Prénom du parent:</label>
          <input
            type="text"
            id="parent-name"
            name="parentName"
            value={parentName}
            onChange={(event) => setParentName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-lastname">Nom du parent:</label>
          <input
            type="text"
            id="parent-lastname"
            name="parentLastname"
            value={parentLastname}
            onChange={(event) => setParentLastname(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-phone">Numéro de téléphone:</label>
          <input
            type="tel"
            id="parent-phone"
            name="parentPhone"
            value={parentPhone}
            onChange={(event) => setParentPhone(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-email">Votre email:</label>
          <input
            type="email"
            id="parent-email"
            name="parentEmail"
            value={parentEmail}
            onChange={(event) => setParentEmail(event.target.value)}
          />
        </div>
        {kids.map((child, index) => (
          <div key={index}>
            <label htmlFor={`child-name-${index}`}>Prénom de l'enfant:</label>
            <input
              type="text"
              id={`child-name-${index}`}
              name="name"
              value={child.name}
              onChange={(event) => handleChange(event, index)}
            />
            <label htmlFor={`child-lastname-${index}`}>Nom de l'enfant:</label>
            <input
              type="text"
              id={`child-lastname-${index}`}
              name="lastname"
              value={child.lastname || ''}
              onChange={(event) => {
                const newKids = [...kids];
                newKids[index] = {
                  ...newKids[index],
                  lastname: event.target.value,
                };
                setKids(newKids);
              }}
            />
            <label htmlFor={`child-age-${index}`}>Age de l'enfant:</label>
            <input
              type="number"
              id={`child-age-${index}`}
              name="age"
              value={child.age}
              onChange={(event) => handleChange(event, index)}
            />
          </div>
        ))}
        <button type="button" onClick={handleAddChild}>
          +
        </button>
        <button type="submit">SUIVANT</button>
      </form>
    </div>
  );}
