import React, { useState } from 'react';

export default function SignUp() {
  const [parentFirstName, setParentFirstName] = useState('');
  const [parentLastName, setParentLastName] = useState('');
  const [parentEmail, setParentEmail] = useState('');
  const [parentPhoneNumber, setParentPhoneNumber] = useState('');
  const [kidName, setKidName] = useState('');
  const [kidLastName, setKidLastName] = useState('');
  const [kidAge, setKidAge] = useState('');
  const [numKids, setNumKids] = useState(1);

  const handleAddKid = () => {
    setNumKids(numKids + 1);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = {
      prenomParent: parentFirstName,
      nomParent: parentLastName,
      tel: parentPhoneNumber,
      mail: parentEmail,
      // prenomEnfant: kidName,
      // nomEnfant: kidLastName,
      // dateNaissance: kidAge
    };

    // Make a POST request to the server
    fetch('http://localhost:8080/addEnfantPreinscri', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (response.ok) {
          // Handle successful response
          console.log('Form submitted successfully');
          // Clear form fields
          setParentFirstName('');
          setParentLastName('');
          setParentEmail('');
          setParentPhoneNumber('');

          // Display success message to user
          alert('Form submitted successfully!');
        } else {
          // Handle error response
          console.error('Error submitting form');
          // Display error message to user
          alert('Error submitting form. Please try again.');
        }
      })
      .catch((error) => {
        // Handle network error
        console.error('Network error:', error);
        // Display error message to user
        alert('Network error. Please try again later.');
      });
  };

  return (
    <div>
      <h1 className='sign-up'>pré-inscrie maintenant!</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="parent-first-name">Prénom du parent:</label>
          <input
            type="text"
            id="parent-first-name"
            value={parentFirstName}
            onChange={(event) => setParentFirstName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-last-name">Nom du parent:</label>
          <input
            type="text"
            id="parent-last-name"
            value={parentLastName}
            onChange={(event) => setParentLastName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-email">Votre email:</label>
          <input
            type="email"
            id="parent-email"
            value={parentEmail}
            onChange={(event) => setParentEmail(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-phone">Numéro de téléphone:</label>
          <input
            type="tel"
            id="parent-phone"
            value={parentPhoneNumber}
            onChange={(event) => setParentPhoneNumber(event.target.value)}
          />
        </div>


        <button type="button" onClick={handleAddKid}>
          Ajouter un enfant
        </button>
        <button type="submit">SUIVANT</button>
      </form>
    </div>
  );
}
