import React, { useState } from 'react';
import '../../App.css';

export default function SignUp() {
  const [numKids, setNumKids] = useState(0);
  const [kids, setKids] = useState([]);
  const [parentName, setParentName] = useState('');
  const [parentPhone, setParentPhone] = useState('');
  const [parentEmail, setParentEmail] = useState('');

  const handleChange = (event, index) => {
    const newKids = [...kids];
    newKids[index] = {
      ...newKids[index],
      [event.target.name]: event.target.value,
    };
    setKids(newKids);
  };

  const handleAddChild = () => {
    setKids([...kids, { name: '', age: '' }]);
    setNumKids(numKids + 1);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // handle form submission here
    console.log({
      kids,
      parentName,
      parentPhone,
      parentEmail,
    });
  };

  return (
    <div>
      <h1 className='sign-up'>pré-inscrie maintenant!</h1>
      <form onSubmit={handleSubmit}>
        {kids.map((child, index) => (
          <div key={index}>
            <label htmlFor={`child-name-${index}`}>Nom et prénom de l'enfant:</label>
            <input
              type="text"
              id={`child-name-${index}`}
              name="name"
              value={child.name}
              onChange={(event) => handleChange(event, index)}
            />
            <label htmlFor={`child-age-${index}`}>Age de l'enfant:</label>
            <input
              type="number"
              id={`child-age-${index}`}
              name="age"
              value={child.age}
              onChange={(event) => handleChange(event, index)}
            />
          </div>
        ))}
        <div>
          <label htmlFor="parent-name">Nom et prénom du parent:</label>
          <input
            type="text"
            id="parent-name"
            name="parentName"
            value={parentName}
            onChange={(event) => setParentName(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-phone">Numéro de téléphone du parent:</label>
          <input
            type="tel"
            id="parent-phone"
            name="parentPhone"
            value={parentPhone}
            onChange={(event) => setParentPhone(event.target.value)}
          />
        </div>
        <div>
          <label htmlFor="parent-email">Email du parent:</label>
          <input
            type="email"
            id="parent-email"
            name="parentEmail"
            value={parentEmail}
            onChange={(event) => setParentEmail(event.target.value)}
          />
        </div>
        <button type="button" onClick={handleAddChild}>
          Ajouter un autre enfant
        </button>
        <button type="submit">SUIVANT</button>
      </form>
    </div>
  );
}
