import React, { useState } from "react";
import axios from "axios";

function SignUp() {
  const [formData, setFormData] = useState([
    {
      parentName: "",
      parentLastName: "",
      parentEmail: "",
      parentNumber: "",
      kidName: "",
      kidLastName: "",
      kidBirthdate: "",
    },
  ]);

  const handleAddForm = () => {
    setFormData([
      ...formData,
      {
        nomParent: "",
        prenomParent: "",
        mail: "",
        tel: "",
        nomEnfant: "",
        prenomEnfant: "",
        dateNaissance: "",
      },
    ]);
  };

  const handleRemoveForm = (index) => {
    const newFormData = [...formData];
    newFormData.splice(index, 1);
    setFormData(newFormData);
  };

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const newFormData = [...formData];
    newFormData[index][name] = value;
    setFormData(newFormData);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:8080/addEnfantPreinscrit", formData)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        {formData.map((input, index) => (
          <div key={index}>
            <input
              type="text"
              name="parentName"
              placeholder="Parent Name"
              value={input.parentName}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="parentLastName"
              placeholder="Parent Last Name"
              value={input.parentLastName}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="email"
              name="parentEmail"
              placeholder="Parent Email"
              value={input.parentEmail}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="tel"
              name="parentNumber"
              placeholder="Parent Number"
              value={input.parentNumber}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="kidName"
              placeholder="Kid Name"
              value={input.kidName}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="text"
              name="kidLastName"
              placeholder="Kid Last Name"
              value={input.kidLastName}
              onChange={(e) => handleInputChange(e, index)}
            />
            <input
              type="date"
              name="kidBirthdate"
              placeholder="Kid Birthdate"
              value={input.kidBirthdate}
              onChange={(e) => handleInputChange(e, index)}
            />
            {index > 0 && (
              <button type="button" onClick={() => handleRemoveForm(index)}>
                Remove
              </button>
            )}
          </div>
        ))}
        <button type="button" onClick={handleAddForm}>
          Add More
        </button>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default SignUp;
