import React, { useState } from 'react';
import axios from 'axios';

export default function SignupActivtie() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [activity, setActivity] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Make a POST request to the backend API with form data
      await axios.post('/addExenfant', {
        firstName,
        lastName,
        email,
        birthdate,
        phoneNumber,
        activity,
      });

      // Reset form fields after successful submission
      setFirstName('');
      setLastName('');
      setEmail('');
      setBirthdate('');
      setPhoneNumber('');
      setActivity('');

      // Optionally, you can display a success message or redirect the user to another page
      console.log('Form submitted successfully');
    } catch (error) {
      console.error('Error submitting form:', error);
      // Handle errors, such as displaying error messages to the user
      if (error.response && error.response.data && error.response.data.errors) {
        setErrors(error.response.data.errors);
      } else {
        setErrors({ general: 'Une erreur s\'est produite lors de l\'envoi du formulaire.' });
      }
    }
  };

  return (
    <div className="signup-form">
      <h1>Inscrivez-vous maintenant!</h1>
      <form onSubmit={handleSubmit}>
        <label>Pr√©nom:</label>
        <input
          type="text"
          value={firstName}
          onChange={(event) => setFirstName(event.target.value)}
          placeholder="First Name"
        />
        {errors.firstName && <p>{errors.firstName}</p>}

        <label>Nom:</label>
        <input
          type="text"
          value={lastName}
          onChange={(event) => setLastName(event.target.value)}
          placeholder="Last Name"
        />
        {errors.lastName && <p>{errors.lastName}</p>}

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
          placeholder="Email"
        />
        {errors.email && <p>{errors.email}</p>}

        <label>Date de naissance:</label>
        <input
          type="date"
          value={birthdate}
          onChange={(event) => setBirthdate(event.target.value)}
          placeholder="birthdate"
        />
        {errors.birthdate && <p>{errors.birthdate}</p>}

        <label>Num√©ro de t√©l√©phone:</label>
        <input
          type="number"
          value={phoneNumber}
          onChange={(event) => setPhoneNumber(event.target.value)}
          placeholder="Number"
        />
        {errors.phoneNumber && <p>{errors.phoneNumber}</p>}

        <label>Activit√©:</label>
        <input
          type="text"
          value={activity}
          onChange={(event) => setActivity(event.target.value)}
          placeholder="Activity"
        />
        {errors.activity && <p>{errors.activity}</p>}

        <button type="submit">S'inscrire</button>
      </form>
      {errors.general && <p>{errors.general}</p>}
    </div>
  );
}
