import React, { useState, useEffect } from 'react';
import './Cards.css';
import CardItem from './CardItem';
import axios from 'axios';

function Cards() {
  const [activities, setActivities] = useState([]); // Initialize an empty array to store activities
  const [loading, setLoading] = useState(true); // Initialize a loading state
  const [error, setError] = useState(null); // Initialize an error state

  useEffect(() => {
    // Make API call to fetch activities when the component mounts
    axios.get('/getActivities') // Replace with your API endpoint URL
      .then(response => {
        setActivities(response.data); // Update the state with the fetched activities
        setLoading(false); // Set the loading state to false
      })
      .catch(error => {
        console.error(error);
        setError(error.message); // Set the error state with the error message
        setLoading(false); // Set the loading state to false
      });
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Display a loading message while the data is being fetched
  }

  if (error) {
    return <div>Error: {error}</div>; // Display an error message if the API call fails
  }

  return (
    <div className='cards'>
      <h1>Explorez nos activit√©s.</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {activities.map(activity => (
              <CardItem
                key={activity.id}
                src={activity.imageLink} // Fix: use imageLink instead of image
                text={activity.description}
                label={activity.label}
                date={activity.date}
                startTime={activity.startTime}
                endTime={activity.endTime}
              />
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Cards;
