import React, { useState } from 'react';
import axios from 'axios'; // Import axios to make HTTP requests

export default function SignupActivtie() {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [birthdate, setBirthdate] = useState(''); // Add state for birthdate
  const [phoneNumber, setPhoneNumber] = useState(''); // Add state for phoneNumber
 
  const [activity, setActivity] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = async (event) => {
    event.preventDefault();
    
    try {
      // Make a POST request to the backend API with form data
      await axios.post('/addExenfant', {
        firstName,
        lastName,
        email,
        birthdate,
        phoneNumber,
        password,
        activity
      });

      // Reset form fields after successful submission
      setFirstName('');
      setLastName('');
      setEmail('');
      setBirthdate('');
      setPhoneNumber('');
      setPassword('');
      setConfirmPassword('');
      setActivity('');

      // Optionally, you can display a success message or redirect the user to another page
      console.log('Form submitted successfully');
    } catch (error) {
      console.error('Error submitting form:', error);
      // Handle errors, such as displaying error messages to the user
    }
  };

  return (
    <div className="signup-form">
      <h1>Inscrivez-vous maintenant!</h1>
      <form onSubmit={handleSubmit}>
        <label>Prénom:</label>
        <input
          type="text"
          value={firstName}
          onChange={(event) => setFirstName(event.target.value)}
          placeholder="First Name"
        />
        <label>Nom:</label>
        <input
          type="text"
          value={lastName}
          onChange={(event) => setLastName(event.target.value)}
          placeholder="Last Name"
        />
        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
          placeholder="Email"
        />
        <label>Date de naissance:</label>
        <input
          type="date"
          value={birthdate}
          onChange={(event) => setBirthdate(event.target.value)}
          placeholder="birthdate"
        />
        <label>Numéro de téléphone:</label>
        <input
          type="number"
          value={phoneNumber}
          onChange={(event) => setPhoneNumber(event.target.value)}
          placeholder="Number"
        />
     
     
        <button type="submit">S'inscrire</button>
      </form>
      {errors && (
        <div className="error-messages">
          {Object.keys(errors).map((key, index) => (
            <p key={index}>{errors[key]}</p>
          ))}
        </div>
      )}
    </div>
  );
}
